INSERT INTO `game` (`id`, `name`, `image`, `text`, `year`, `download`, `visible`, `dev_id`) VALUES
(1, 'Brawl Stars', '../img/01.png', 'В этой игре все что вам нужно это уничтожить противника, и использовать необходимо вооружение своего персонажа. Персонажи здесь самые разнообразные и имеют свои уникальные особенности, одни хорошо стреляют другие же могут избить вас голыми руками!', '2018-12-08', 'https://play.google.com/store/apps/details?id=com.supercell.brawlstars', 1, 22),
(2, 'Monopoly', '../img/02.png', 'Monopoly – популярная настольная игра на андроид, в которую вы сможете играть всей семьей. В этой игре вы будете покупать, продавать и прокладывать свой путь к богатству. Играйте в самую популярную игру в мире на своем телефоне или планшете с друзьями и добивайтесь отличных результатов.', '2019-12-04', 'https://play.google.com/store/apps/details?id=com.marmalade.monopoly', 1, 0),
(3, 'Nonogram.com', '../img/03.png', 'Откройте для себя увлекательную игру Nonogram! Решайте японские кроссворды с простыми правилами и отгадывайте зашифрованное изображение! Разгадайте спрятанное изображение на доске, раскрашивая клетки как по горизонтали, так и по вертикали!', '2019-08-02', 'https://play.google.com/store/apps/details?id=com.easybrain.nonogram&hl=ru&gl=US', 1, 0),
(4, 'Pixel Dungeon', '../img/04.png', 'Pixel Dungeon - это игра в жанре roguelike с пиксельной графикой и простым управлением. Исследуйте глубины Пиксельного Подземелья, собирайте полезные предметы и сокровища, бейтесь с монстрами, чтобы найти Амулет Йендора.', '2015-02-28', 'https://play.google.com/store/apps/details?id=com.watabou.pixeldungeon', 1, 0),
(5, 'Geometry Dash', '../img/05.png', 'Geometry Dash – это хардкорная 2D-аркада, которая по своей динамике жутко напоминает Flappy Bird и подобные ей аркады. Абсолютно так же выводит из себя и тоже не позволяет оторваться ни на секунду, пока хотя бы один злополучный уровень не будет полностью пройден.', '2013-08-12', 'https://play.google.com/store/apps/details?id=com.robtopx.geometryjump', 1, 0),
(6, 'Soul Knight', '../img/06.png', '«Это эпоха меча и револьвера. Магический камень, который поддерживал в нашем мире равновесие, украден высокотехнологичной расой пришельцев. Судьба мира висит на волоске. Сумеете ли вы вернуть артефакт? Все зависит от вас...»', '2017-02-16', 'https://play.google.com/store/apps/details?id=com.ChillyRoom.DungeonShooter', 1, 0),
(7, 'Leap Day', '../img/07.png', 'Leap Day - Игра отлично подойдет для любителей олдскульных платформеров с их невероятной сложностью. Leap Day сумеет вас покорить сочетанием ретро-элементов с оригинальной механикой геймплея. В игре вам предстоит двигаться постоянно вверх. В игре вы встретите большое количество уровней и много ловушек.', '2016-05-11', 'https://play.google.com/store/apps/details?id=com.nitrome.leapday', 1, 0),
(8, 'Redungeon', '../img/08.png', 'Redungeon – хардкорная игра на андроид, от компании Nitrome. В этой игре вы возьмете под свой контроль отважного рыцаря, который решил в одиночку пройти через все опасные подземелья, таящие в себе большое количество препятствий и врагов.', '2016-06-22', 'https://play.google.com/store/apps/details?id=com.nitrome.redungeon', 1, 0),
(9, 'SSG 2', '../img/09.png', 'SSG 2 - Динамическая аркада на андроид. Выберите себе героя, настройте его внешний вид, и вперед! Игра основана на классическом гольфе, только на этот раз вы будете «проводить» шар в лузу по очень заковыристым уровням. Чем меньше попыток вы потратите, тем больше очков вы получите.', '2013-03-13', 'https://play.google.com/store/apps/details?id=com.noodlecake.ssg2&hl=en&gl=US', 1, 0),
(10, 'Basketball', '../img/10.png', 'Bouncy Basketball - двумерная баскетбольная игра, основанная на физике, с помощью одной кнопки. Бросьте вызов своим любителям баскетбола на ожесточенные баскетбольные баталии на общем экране или просто играйте против процессора, если не хотите ставить под угрозу дружбу.', '2015-04-03', 'https://play.google.com/store/apps/details?id=com.DreamonStudios.BouncyBasketball&hl=ru&gl=US', 1, 0),
(11, 'Antiyoy', '../img/11.png', 'Antiyoy -  довольно простая на первый взгляд игра, но чем дольше вы в неё играете тем, больше приходит осознание того, что в игре очень глубокий геймплей, который способен затянуть игрока на долгое время.', '2015-11-20', 'https://play.google.com/store/apps/details?id=yio.tro.antiyoy.android&hl=ru&gl=US', 1, 22),
